use Cwd;my $dir = getcwd();opendir(DIR, $dir) || die "Can't open $some_dir: $!";print "Enter string to search: \n";my $str = <STDIN>;chomp $str;while (my $file = readdir(DIR)) {		next unless (-d "$dir/$file");if(!($file eq "." || $file eq "..")){    if(!defined($pid=fork()))    {        die "cannot fork a child : $!";    }    elsif ($pid == 0)    {	print "-----------------\n";        print "Searching in sub-directory $file \n";	        system("grep -r $str $file/");                	print "Finished Searching in subdirectory $file\n";	$file = exec(`cd -r $file/*`);	exit();	    }    else    {        while(wait()!= -1){		}    }}}closedir(DIR);    exit 0;